var chars = create_pointer(0xD12A, 1)

var tick_callbacks = stackalloc func<void>[10]
var tick_callback_count = 0

// Register a callback
void register_tick_callback(func<void> callback) {
    tick_callbacks[tick_callback_count++] = callback
}

// Call all the callbacks
void tick() {
    for (int i = 0; i < tick_callback_count; i++) {
        tick_callbacks[i]()
    }
}

// Register a callback that clears the screen
register_tick_callback(() => {
    for (int i = 0; i < 17 * 17; i++) {
        chars[i] = ' '
    }
})

// Register a callback that draws a 'x' on the screen
// and moves it to the right every time it is called
var offset = 0

register_tick_callback(() => {
    chars[offset++] = 'x'
})

// Tick 3 times
tick()
tick()
tick()

// The 'x' should be on the 3rd column

int allocate() {
    // Allocate 10 integers on the stack
    int[] numbers = stackalloc int[10]

    // Return the address of the stack allocation
    return get_address(numbers)
}

// The following call will return address 61294
allocate()

void allocate_call() {
    // The following call will return address 61294
    allocate()

    // Because the stack is reset after a function returns,
    // the following call allocation will also return address 61294
    int[] numbers = stackalloc int[10]

    // The following call will return address 61304
    // because we allocated 10 integers in this function
    allocate()
}

allocate_call()

// Utility function to get the address of a stack allocation
inline int get_address(int[] pointer) => asm { AIN @pointer }

